# first solution
def reverse_words(sentence):
    words = []

    counter = 0
    sentence_length = len(sentence)
    if sentence_length <= 0:
        return sentence
    current_word = ""  #
    # "1234 abc XYZ"
    while counter < sentence_length:  # 0
        current_char = sentence[counter]  # 1
        next_char = None
        if counter + 1 < sentence_length:
            next_char = sentence[counter + 1]

        print(f"current_char: {current_char}, next_char: {next_char}")
        if current_char != " ":
            current_word += current_char
            if next_char is None or next_char == " ":
                words.append(current_word)
                current_word = ""
                counter += 1
                continue
        counter += 1
    return " ".join(words[::-1])


# Second solution
def reverse_words_two_pointers(sentence):
    words = sentence.split()

    left = 0
    right = len(words) - 1

    while left < right:
        words[left], words[right] = words[right], words[left]
        left += 1
        right -= 1

    return " ".join(words)


